   104→/**
   105→ * Get the GitHub directory for a project
   106→ */
   107→function getGitHubDir(project: Project): string {
   108→  return path.join(project.path, '.auto-claude', 'github');
   109→}
   110→
   111→/**
   112→ * Get the auto-fix config for a project
   113→ */
   114→function getAutoFixConfig(project: Project): AutoFixConfig {
   115→  const configPath = path.join(getGitHubDir(project), 'config.json');
   116→
   117→  // Use try/catch instead of existsSync to avoid TOCTOU race condition
   118→  try {
   119→    const data = JSON.parse(fs.readFileSync(configPath, 'utf-8'));
   120→    return {
   121→      enabled: data.auto_fix_enabled ?? false,
   122→      labels: data.auto_fix_labels ?? ['auto-fix'],
   123→      requireHumanApproval: data.require_human_approval ?? true,
   124→      botToken: data.bot_token,
   125→      model: data.model ?? 'claude-sonnet-4-20250514',
   126→      thinkingLevel: data.thinking_level ?? 'medium',
   127→    };
   128→  } catch {
   129→    // File doesn't exist or is invalid - return defaults
   130→  }
   131→
   132→  return {
   133→    enabled: false,
   134→    labels: ['auto-fix'],
   135→    requireHumanApproval: true,
   136→    model: 'claude-sonnet-4-20250514',
   137→    thinkingLevel: 'medium',
   138→  };
   139→}
