   144→function saveAutoFixConfig(project: Project, config: AutoFixConfig): void {
   145→  const githubDir = getGitHubDir(project);
   146→  fs.mkdirSync(githubDir, { recursive: true });
   147→
   148→  const configPath = path.join(githubDir, 'config.json');
   149→  let existingConfig: Record<string, unknown> = {};
   150→
   151→  // Use try/catch instead of existsSync to avoid TOCTOU race condition
