   163→    bot_token: config.botToken,
   164→    model: config.model,
   165→    thinking_level: config.thinkingLevel,
   166→  };
   167→
   168→  fs.writeFileSync(configPath, JSON.stringify(updatedConfig, null, 2));
   169→}
