   174→function getAutoFixQueue(project: Project): AutoFixQueueItem[] {
   175→  const issuesDir = path.join(getGitHubDir(project), 'issues');
   176→
   177→  // Use try/catch instead of existsSync to avoid TOCTOU race condition
   178→  let files: string[];
   179→  try {
   180→    files = fs.readdirSync(issuesDir);
   181→  } catch {
   182→    // Directory doesn't exist or can't be read
   183→    return [];
   184→  }
   185→
   186→  const queue: AutoFixQueueItem[] = [];
   187→
   188→  for (const file of files) {
   189→    if (file.startsWith('autofix_') && file.endsWith('.json')) {
   190→      try {
   191→        const data = JSON.parse(fs.readFileSync(path.join(issuesDir, file), 'utf-8'));
   192→        queue.push({
   193→          issueNumber: data.issue_number,
   194→          repo: data.repo,
   195→          status: data.status,
   196→          specId: data.spec_id,
   197→          prNumber: data.pr_number,
   198→          error: data.error,
   199→          createdAt: data.created_at,
   200→          updatedAt: data.updated_at,
   201→        });
   202→      } catch {
   203→        // Skip invalid files
   204→      }
   205→    }
   206→  }
   207→
   208→  return queue.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
   209→}
